import React, { useState } from 'react';

export const UpdateUser = () => {
  const [showForm, setShowForm] = useState(false);
  const [isUpdate, setIsUpdate] = useState(false); // New state to determine if it's an update operation

  // Function to show the Add User form
  const handleAddUserClick = () => {
    setShowForm(true);
    setIsUpdate(false); // Ensure we're in "add" mode
  };

  // Function to show the Update User form (you might need additional logic here to load the user's data into the form)
  const handleUpdateUserClick = () => {
    setShowForm(true);
    setIsUpdate(true); // Switch to "update" mode
  };

  return (
    <div>
      {/* Add User Button */}
      <button onClick={handleAddUserClick} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mt-4">
        Add User
      </button>

      {/* Update User Button - Simulate this for demonstration. In practice, this might be dynamically generated per user row */}
      <button onClick={handleUpdateUserClick} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded ml-4">
        Update User
      </button>

      {showForm && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75">
          <div className="bg-white p-8 rounded shadow">
            <h2 className="text-xl font-bold mb-4">{isUpdate ? "Update User" : "Add New User"}</h2>
            <form>
              {/* Form fields here */}
              <div className="flex items-center justify-between">
                <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                  {isUpdate ? "Update" : "Submit"}
                </button>
                <button type="button" onClick={() => setShowForm(false)} className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                  Back
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UpdateUser;
