import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export const UpdateUser = () => {
    const [inputUser, setInputUser] = useState({
        name: "",
        email: "",
        phonenumber: "",
        hobbies: ""
    });
    const [showForm, setShowForm] = useState(false);

    const { id } = useParams();

    useEffect(() => {
        const fetchSingleUser = async () => {
            const res = await axios.get(`http://localhost:3000/read/${id}`);
            setInputUser({
                name: res.data.name,
                email: res.data.email,
                phonenumber: res.data.phonenumber,
                hobbies: res.data.hobbies
            });
        };

        fetchSingleUser();
    }, [id]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setInputUser(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.put(`http://localhost:3000/updateuserdata/${id}`, inputUser);
        setShowForm(false); // Close the modal on successful submission
    };

    return (
        <div>
            <button onClick={() => setShowForm(true)}>Update User</button>
            {showForm && (
                <div className="modalBackground">
                    <div className="modalContainer">
                        <button onClick={() => setShowForm(false)}>X</button>
                        {/* The rest of your form goes here */}
                    </div>
                </div>
            )}
        </div>
    );
};

export default UpdateUser;
