import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';

export const UpdateUser = () => {
    const [inputUser, setInputUser] = useState({
        name: "", 
        email: "", 
        phonenumber: "", 
        hobbies: ""
    });
    const { id } = useParams(); // Get the user ID from the URL
    const history = useHistory(); // For redirecting after update

    useEffect(() => {
        if (id) {
            fetchSingleUser();
        }
    }, [id]);

    const fetchSingleUser = async () => {
        const res = await axios.get(`http://localhost:3000/read/${id}`);
        setInputUser({
            name: res.data.name, 
            email: res.data.email, 
            phonenumber: res.data.phonenumber, 
            hobbies: res.data.hobbies
        });
    }; 

    const handleChange = (event) => {
        setInputUser({
            ...inputUser,
            [event.target.name]: event.target.value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault(); 
        await axios.put(`http://localhost:3000/updateuserdata/${id}`, inputUser);
        history.push('/'); // Assuming the main page where the table is shown is the root
    };

    if (!id) {
        return null; // Or history.push('/') to redirect
    }

    return (
        <div className="bg-white p-8 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Update User</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">Name</label>
                    <input type="text" id="name" name="name" required value={inputUser.name} onChange={handleChange} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                </div>
                <div className="mb-4">
                    <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">Email</label>
                    <input type="email" id="email" name="email" required value={inputUser.email} onChange={handleChange} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                </div>
                <div className="mb-4">
                    <label htmlFor="phonenumber" className="block text-gray-700 text-sm font-bold mb-2">Phone Number</label>
                    <input type="text" id="phoneNumber" name="phonenumber" required value={inputUser.phonenumber} onChange={handleChange} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                </div>
                <div className="mb-4">
                    <label htmlFor="hobbies" className="block text-gray-700 text-sm font-bold mb-2">Hobbies</label>
                    <input type="text" id="hobbies" name="hobbies" required value={inputUser.hobbies} onChange={handleChange} className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                </div>
                <div className="flex items-center justify-between">
                    <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Submit</button>
                    <button type="button" onClick={() => history.push('/')} className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Back</button>
                </div>
            </form>
        </div>
    );
};

export default UpdateUser;
